package corejava;

public class Operations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           
		int x=5 , y=10, a, b, c, d;
		a = x+y*2;
		b = x-y+2;
		c = (x+y)*2;
		d = y%x;
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
				
		
		
	}

}
